import React, { useState, useEffect } from "react";
import axios from "axios";
import { css } from "emotion";


function FlightReview() {
  const [data, setData] = useState([]);
  const [background, setBackground] = useState("#fdfdfd");
  const [font, setFont] = useState("#424246");


const setStyle = (background, font) => {
    setBackground(background);
    setFont(font);
  };

  const card = css`
    max-width: 600px;
    padding: 40px 40px 20px 40px;
    background-color: ${background};
    h1 {
      font-family: "Abril Fatface", cursive;
      font-weight: 400;
      font-size: 36px;
      margin-bottom: 10px;
      color: ${font};
    }
    p {
      font-family: "Poppins", sans-serif;
      font-weight: 300;
      font-size: 16px;
      margin-bottom: 20px;
      color: ${font};
    }
  `;

  const cardButtons = css`
    display: flex;
    flex-wrap: wrap;

    div {
      font-family: "Poppins", sans-serif;
      font-weight: 500;
      font-size: 12px;
      padding: 10px 30px;
      margin-right: 20px;
      margin-bottom: 20px;
      border: none;
      border-radius: 50px;
      cursor: pointer;
    }
  `;

  const blackButton = css`
  color: #fdfdfd;
  background-color: #424246;
`;

const blueButton = css`
  color: #fdfdfd;
  background-color: #1d499b;
`;

const yellowButton = css`
  color: #424246;
  background-color: #f9d648;
`;

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      console.log(result.data);
