      <thead>
          {/* search from comments, sortable table, anon user post */}
        <tr>
          <th>Flight</th>
          <th>Rating</th>
          <th>Comment</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item["id"]}>
            <td>{item["flight"]}</td>
            <td>{item["flightRating"]}</td>
