import React, { useState, useEffect } from "react";
import { css } from "emotion";
import axios from "axios";
import AddReview from "./AddReview.js"

const FlightReview = () => {

  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      console.log(result.data);
      setData(result.data);
    };

    fetchData();
  }, []);

  const card = css`
    max-width: 600px;
    padding: 40px 40px 20px 40px;
    background-color: #ffffff;
    h1 {
      font-family: "Abril Fatface", cursive;
      font-weight: 400;
      font-size: 36px;
      margin-bottom: 10px;
    }
    p {
      font-family: "Poppins", sans-serif;
      font-weight: 300;
      font-size: 16px;
      margin-bottom: 20px;
    }
  `;

  const cardButtons = css`
    display: flex;
    flex-wrap: wrap;

    div {
      font-family: "Poppins", sans-serif;
      font-weight: 500;
      font-size: 12px;
      padding: 10px 30px;
      margin-right: 20px;
      margin-bottom: 20px;
      border: none;
      border-radius: 50px;
      cursor: pointer;
    }
  `;


  const blueButton = css`
    color: #fdfdfd;
    background-color: #1d499b;
  `;


  return (
    <div className={card}>
      <h1>Reviews</h1>         
      <div className={cardButtons}>
        <div className={blueButton}>
          Add Review
        </div>
      </div>
        <table>
      <thead>
          {/* search from comments, sortable table, anon user post */}
        <tr>
          <th>Flight</th>
          <th>Rating</th>
          <th>Comment</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item["id"]}>
            <td>{item["flight"]}</td>
            <td>{item["flightRating"]}</td>
            <td>{item["comment"]}</td>
            <td>{item["user"]}</td>
          </tr>
        ))}
      </tbody>
    </table>

    </div>
  );
};

export default FlightReview;
