import React, { useState, useEffect } from "react";
import { css } from "emotion";
import axios from "axios";
import AddReview from "./AddReview.js"

const FlightReview = () => {
  const [background, setBackground] = useState("#fdfdfd");

  const [font, setFont] = useState("#424246");

  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      console.log(result.data);
      setData(result.data);
    };

    fetchData();
  }, []);

  const setStyle = (background, font) => {
    setBackground(background);
    setFont(font);
  };

  const card = css`
    max-width: 600px;
    padding: 40px 40px 20px 40px;
    background-color: ${background};
    h1 {
      font-family: "Abril Fatface", cursive;
      font-weight: 400;
      font-size: 36px;
      margin-bottom: 10px;
      color: ${font};
    }
    p {
      font-family: "Poppins", sans-serif;
      font-weight: 300;
      font-size: 16px;
      margin-bottom: 20px;
      color: ${font};
    }
  `;

  const cardButtons = css`
    display: flex;
    flex-wrap: wrap;

    div {
      font-family: "Poppins", sans-serif;
      font-weight: 500;
      font-size: 12px;
      padding: 10px 30px;
      margin-right: 20px;
      margin-bottom: 20px;
      border: none;
      border-radius: 50px;
      cursor: pointer;
    }
  `;

  const blackButton = css`
    color: #fdfdfd;
    background-color: #424246;
  `;

  const blueButton = css`
    color: #fdfdfd;
    background-color: #1d499b;
  `;

  const yellowButton = css`
    color: #424246;
    background-color: #f9d648;
  `;

  return (
    <div className={card}>
      <h1>Your Life is Your Message</h1>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit. Pulvinar sed
        elementum egestas blandit. Proin augue lacus, phasellus vel lorem risus.
      </p>
      <div className={cardButtons}>
        <div
          onMouseEnter={() => setStyle("#424246", "#fdfdfd")}
          onMouseOut={() => setStyle("#fdfdfd", "#424246")}
          className={blackButton}
        >
          Black
        </div>
        <div
          onMouseEnter={() => setStyle("#1d499b", "#fdfdfd")}
          onMouseOut={() => setStyle("#fdfdfd", "#424246")}
          className={blueButton}
        >
          Blue
        </div>
        <div
          onMouseEnter={() => setStyle("#f9d648", "#424246")}
          onMouseOut={() => setStyle("#fdfdfd", "#424246")}
          className={yellowButton}
        >
          Yellow
        </div>
        <table>
      <thead>
          {/* search from comments, sortable table, anon user post */}
        <tr>
          <th>Flight</th>
          <th>Rating</th>
          <th>Comment</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item["id"]}>
            <td>{item["flight"]}</td>
            <td>{item["flightRating"]}</td>
            <td>{item["comment"]}</td>
            <td>{item["user"]}</td>
          </tr>
        ))}
      </tbody>
    </table>
      </div>
    </div>
  );
};

export default FlightReview;
