import React, { useState, useEffect } from "react";
import axios from "axios";
import AddReview from "./AddReview.js" 
import Card from "react-bootstrap/Card";


const FlightReview = () => {

  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      setData(result.data);
    };

    fetchData();
  }, []);

  return (
      <Card>
          <Card.Body>
    <div className="card">
      <h1>Reviews</h1>         
      <div className="reviews">
        <div>
            <AddReview />
        </div>
      </div>
        <table>
      <thead>
          {/* search from comments, sortable table, anon user post */}
        <tr>
          <th>Flight</th>
          <th>Rating</th>
          <th>Comment</th>
          <th>Username</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item["id"]}>
            <td>{item["flight"]}</td>
            <td>{item["flightRating"]}</td>
            <td>{item["comment"]}</td>
            <td>{item["user"]}</td>
          </tr>
        ))}
      </tbody>
    </table>

    </div>
    </Card.Body>
    </Card>
  );
};

export default FlightReview;
