import React, { useState } from "react";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import axios from "axios";

const AddReview = ({refreshData}) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [form, setForm] = useState();

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.currentTarget.name]: e.currentTarget.value,
    });
  };
  const handleSubmit = (event) => {
    console.log({ form });
    handleClose()
    refreshData(true)
  };

  axios.post("localhost:8080/reviews"[{form}])


  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Add Review
      </Button>
      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Add Review</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <div>
              <label>Flight Name</label>
              <input type="text" name="flight" onChange={handleChange} />
              <label>
                Flight Rating
                <select name="flight_rating" onChange={handleChange}>
                  <option value="1">1</option>
                  <option value="2">2</option>
                  <option value="3">3</option>
                  <option value="4">4</option>
                  <option value="5">5</option>
                </select>
              </label>
            </div>
            <div>
              <label>Comment</label>
              <input type="text" name="comment" onChange={handleChange} />
            </div>
            <button type="button" onClick={handleSubmit}>
              Submit
            </button>
          </form>
        </Modal.Body>
      </Modal>
    </>
  );
};
export default AddReview;
