import React, { useState, useEffect } from "react";
import axios from "axios";
import AddReview from "./AddReview.js";
import Card from "react-bootstrap/Card";
import BootstrapTable from 'react-bootstrap-table-next';


const FlightReview = () => {
  const [data, setData] = useState([]);
  const [refresh, setRefresh] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      setData(result.data);
    };
    if (refresh === true) {
      fetchData();
      setRefresh(false);
    }
  }, [refresh]);

//   var products = [{
//     id: 1,
//     name: "Product1",
//     price: 120
// }, {
//     id: 2,
//     name: "Product2",
//     price: 80
// }];



  const columns = [{
    dataField: 'id',
    text: 'Flight',
    sort: true
  }, {
    dataField: 'rating',
    text: 'Rating',
    sort: true
  }, {
    dataField: 'Comment',
    text: 'Comment',
    sort: true
  },
  {
    dataField: 'user',
    text: 'User',
    sort: true
  }];

  return (
    <Card>
      <Card.Body>
        <div className="card">
          <h1>Reviews</h1>
          <div className="reviews">
            <div>
              <AddReview refreshData={setRefresh} />
            </div>
            <div>
            </div>
          </div>
          <table>
            <thead>
              {/* search from comments, sortable table, anon user post */}
              <tr>
                <th>Flight</th>
                <th>Rating</th>
                <th>Comment</th>
                <th>Username</th>
              </tr>
            </thead>
            <tbody>
              {data.map((item) => (
                <tr key={item["id"]}>
                  <td>{item["flight"]}</td>
                  <td>{item["flightRating"]}</td>
                  <td>{item["comment"]}</td>
                  <td>{item["user"]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <BootstrapTable keyField='id' data={ products } columns={ columns } />
      </Card.Body>
    </Card>
  );
};

export default FlightReview;
