import React, { useState, useEffect } from "react";
import axios from "axios";
import AddReview from "./AddReview.js";
import Card from "react-bootstrap/Card";
import BootstrapTable from "react-bootstrap-table-next";

const FlightReview = () => {
  const [data, setData] = useState([]);
  const [refresh, setRefresh] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios("http://localhost:8080/reviews");
      setData(result.data);
    };
    if (refresh === true) {
      fetchData();
      setRefresh(false);
    }
  }, [refresh]);

  //   var products = [{
  //     id: 1,
  //     name: "Product1",
  //     price: 120
  // }, {
  //     id: 2,
  //     name: "Product2",
  //     price: 80
  // }];

  var products = [];

  data.map((item) =>
    products.push({
      id: item["id"],
      flightRating: item["flightRating"],
      flight: item["flight"],
      comment: item["comment"],
      user: item["user"],
    })
  );


  const columns = [
    {
      dataField: "flight",
      text: "Flight",
      sort: true,
    },
    {
      dataField: "flightRating",
      text: "Rating",
      sort: true,
    },
    {
      dataField: "comment",
      text: "Comment",
      sort: true,
    },
    {
      dataField: "user",
      text: "User",
      sort: true,
    },
  ];

  return (
    <Card>
      <Card.Body>
        <div className="card">
          <h1>Reviews</h1>
          <div className="reviews">
            <div>
              <AddReview refreshData={setRefresh} />
            </div>
            <div>
              <SearchBar />
            </div>
          </div>
          <BootstrapTable keyField="id" data={products} search columns={columns} />
        </div>
      </Card.Body>
    </Card>
  );
};

export default FlightReview;
